def checker(guess, answer, attempts):
    if guess == answer:
        print(f"You got it right! You had {attempts} attempts left")
        return -2
    elif guess > answer:
        print(f"Too high! you have {attempts - 1} attempts left")
        attempts = attempts - 1
        return attempts
    elif guess < answer:
        print(f"Too low! you have {attempts - 1} attempts left")
        attempts = attempts - 1
        return attempts
    else:
        print("Something went wrong")
        return -1

print(logo)
print("I'm thinking of a number between 1 and 100.")
pick = r.randint(1, 100)
difficulty = input("Choose a difficulty. Type 'easy' or 'hard': ")
if difficulty == "easy":
    attempts = 10
elif difficulty == "hard":
    attempts = 5
else:
    attempts = 7
    print("Something went wrong so you got 7 attempts")

print (f"You have {attempts} attempts, make them count")
while True:
    guess = int(input("Guess a number: "))
    attempts = checker(guess, pick, attempts)
    if attempts == -2:
        break
    elif attempts == -1:
        continue
    elif attempts == 0:
        print("You ran out of attempts, you lose")
        break
    else:
        print(f"You have {attempts} attempts left")